// Helper function to calculate the cart total
function calculateCartTotal() {
  $total = 0.00;
  if (isset($_SESSION['cart'])) {
    foreach ($_SESSION['cart'] as $item) {
      $total += $item['price'];
    }
  }
  return $total;
}

