 * @return string A secure random password.
 */
function generateRandomPassword($length = 12) {
    $characters = 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789!@#$%^&*()_+-={}[]|;:\'",<.>?/';
    $password = '';
    $passwordLength = $length;
    $max = strlen($characters) - 1;
    
