function forgotPassword($email)
{
    // 1. Sanitize and Validate Input
    $email = filter_var($email, FILTER_SANITIZE_EMAIL);
    if (empty($email)) {
        error_log("Error: Email address is empty."); // Log for debugging.  Don't display to user.
