  // 5.  Send an email with a reset link
  $to = $email;
  $subject = 'Password Reset';
  $message = "Please click on the following link to reset your password: " . $_SERVER['REQUEST_SCHEME'] . "://yourdomain.com/reset?token=" . $token;
