    $sql = "INSERT INTO password_resets (user_id, token, expires_at) VALUES (?, ?, NOW())";
    $stmt = $conn->prepare($sql);
    $stmt->bind_param("iis", $userId, $resetToken, $resetToken); // i = integer, s = string
