<?php

class Database {
    private $host;
    private $db;
    private $user;
    private $pass;

    function __construct() {
        $this->host = 'localhost';
        $this->db = 'example';
        $this->user = 'root';
        $this->pass = '';
    }

    function connect() {
        try {
            $conn = new PDO("mysql:host=$this->host;dbname=$this->db", $this->user, $this->pass);
            return $conn;
        } catch(PDOException $e) {
            echo "Connection failed: " . $e->getMessage();
        }
    }

    function disconnect($conn) {
        $conn = null;
    }
}
?>


<?php

class PurchaseModel extends Database {
    private $conn;

    function __construct() {
        parent::__construct();
        $this->conn = $this->connect();
    }

    // Get all purchases
    function getAllPurchases() {
        $sql = "SELECT * FROM purchases";
        try {
            $stmt = $this->conn->prepare($sql);
            $stmt->execute();
            return $stmt->fetchAll(PDO::FETCH_ASSOC);
        } catch(PDOException $e) {
            echo "Error: " . $e->getMessage();
        }
    }

    // Get purchase by id
    function getPurchaseById($id) {
        $sql = "SELECT * FROM purchases WHERE id = :id";
        try {
            $stmt = $this->conn->prepare($sql);
            $stmt->bindParam(':id', $id);
            $stmt->execute();
            return $stmt->fetch(PDO::FETCH_ASSOC);
        } catch(PDOException $e) {
            echo "Error: " . $e->getMessage();
        }
    }

    // Create a new purchase
    function createPurchase($data) {
        $sql = "INSERT INTO purchases (user_id, product_id, purchase_date, total) VALUES (:user_id, :product_id, NOW(), :total)";
        try {
            $stmt = $this->conn->prepare($sql);
            $stmt->bindParam(':user_id', $data['user_id']);
            $stmt->bindParam(':product_id', $data['product_id']);
            $stmt->bindParam(':total', $data['total']);
            return $stmt->execute();
        } catch(PDOException $e) {
            echo "Error: " . $e->getMessage();
        }
    }

    // Update an existing purchase
    function updatePurchase($id, $data) {
        $sql = "UPDATE purchases SET user_id = :user_id, product_id = :product_id, total = :total WHERE id = :id";
        try {
            $stmt = $this->conn->prepare($sql);
            $stmt->bindParam(':id', $id);
            $stmt->bindParam(':user_id', $data['user_id']);
            $stmt->bindParam(':product_id', $data['product_id']);
            $stmt->bindParam(':total', $data['total']);
            return $stmt->execute();
        } catch(PDOException $e) {
            echo "Error: " . $e->getMessage();
        }
    }

    // Delete a purchase
    function deletePurchase($id) {
        $sql = "DELETE FROM purchases WHERE id = :id";
        try {
            $stmt = $this->conn->prepare($sql);
            $stmt->bindParam(':id', $id);
            return $stmt->execute();
        } catch(PDOException $e) {
            echo "Error: " . $e->getMessage();
        }
    }

    function disconnect() {
        parent::disconnect($this->conn);
    }
}
?>


