function handleSubmit() {
  global $conn;
  // get product ID and quantity from form data
  $productId = $_POST['product_id'];
  $quantity = $_POST['quantity'];

  // add item to order
  addItemToOrder($productId, $quantity);

  // redirect to checkout page
  header('Location: checkout.php');
}

