// Function to display products
function display_products() {
  global $conn;
  $sql = "SELECT * FROM products";
  $result = $conn->query($sql);
  while ($row = $result->fetch_assoc()) {
      echo "<li>" . $row["name"] . " - $" . $row["price"] . "</li>";
  }
