function get_customer_id($user_id) {
    global $conn;

    $sql = "SELECT id FROM customers WHERE user_id = '$user_id'";
    $result = mysqli_query($conn, $sql);

    if (mysqli_num_rows($result) > 0) {
        return mysqli_fetch_assoc($result)['id'];
    } else {
        // Create new customer
        $customer = [
            'id' => null,
