class Product {
    private $id;
    private $name;
    private $price;

    public function __construct($id, $name, $price) {
        $this->id = $id;
        $this->name = $name;
        $this->price = $price;
    }

    public static function getAll() {
        // database connection
        $conn = new mysqli(DB_HOST, DB_USER, DB_PASSWORD, DB_NAME);

        if ($conn->connect_error) {
            die("Connection failed: " . $conn->connect_error);
        }

        $sql = "SELECT * FROM products";
        $result = $conn->query($sql);

        $products = array();
        while ($row = $result->fetch_assoc()) {
            $product = new Product($row['id'], $row['name'], $row['price']);
            $products[] = $product;
        }

        return $products;
    }

    public function addProduct() {
        // database connection
        $conn = new mysqli(DB_HOST, DB_USER, DB_PASSWORD, DB_NAME);

        if ($conn->connect_error) {
            die("Connection failed: " . $conn->connect_error);
        }

        $sql = "INSERT INTO products (name, price) VALUES (?, ?)";
        $stmt = $conn->prepare($sql);
        $stmt->bind_param("ss", $this->name, $this->price);

        if ($stmt->execute()) {
            echo "Product added successfully!";
        } else {
            echo "Error adding product: " . $conn->error;
        }
    }

    public function makePurchase() {
        // database connection
        $conn = new mysqli(DB_HOST, DB_USER, DB_PASSWORD, DB_NAME);

        if ($conn->connect_error) {
            die("Connection failed: " . $conn->connect_error);
        }

        $sql = "INSERT INTO orders (product_id, customer_name, order_date, total_cost) VALUES (?, ?, NOW(), ?)";
        $stmt = $conn->prepare($sql);
        $stmt->bind_param("si", $this->id, $_POST['customer_name'], $this->price);

        if ($stmt->execute()) {
            echo "Order placed successfully!";
        } else {
            echo "Error placing order: " . $conn->error;
        }
    }
}

