    if (add_product_to_cart($product_id)) {
        if (checkout()) {
            echo "Product purchased successfully.";
            return true;
        } else {
            echo "Checkout failed.";
            return false;
        }
    } else {
        echo "Failed to add product to cart.";
        return false;
    }
}

