<?php
function connect_to_db() {
    $conn = new PDO("mysql:host=".DB_HOST.";dbname=".DB_NAME, DB_USERNAME, DB_PASSWORD);
    return $conn;
}

function execute_query($query) {
    try {
        $stmt = $conn->prepare($query);
        $stmt->execute();
        return $stmt;
    } catch (PDOException $e) {
        echo "Error: " . $e->getMessage() . "<br>";
        die();
    }
}
