    $stmt->bindParam(':product_id', $product_id);
    $stmt->bindParam(':quantity', $quantity);
    return $stmt->execute();
}

function create_order($customer_name) {
    global $pdo;
    $stmt = $pdo->prepare("INSERT INTO orders (customer_name, total) VALUES (:customer_name, 0)");
