            $query = "INSERT INTO orders (customer_name, total_cost, date, status) VALUES ('$customer_name', '$total_cost', NOW(), '$order_status')";
            $conn->query($query);

            echo "Order placed successfully.";
        }
    } else {
        echo "Cart is empty.";
    }
}

