// Function to get cart contents
function get_cart_contents($cart) {
    $contents = array();
    foreach ($cart as $product_id => $quantity) {
        $contents[] = array(
            'product_id' => $product_id,
            'quantity' => $quantity
        );
    }
    return $contents;
