    global $_SESSION;

    // Check if the item is already in the cart
    foreach ($_SESSION['cart'] as &$item) {
        if ($item['id'] == $id) {
            // If it is, increment the quantity and update the total cost
            $item['quantity'] += 1;
            $_SESSION['total_cost'] -= $item['price'];
