            $product_id = $_POST['product_id'];
            $new_quantity = (int) $_POST['new_quantity'];
            $this->cart->updateQuantity($product_id, $new_quantity);
            echo 'Quantity updated.';
        } else {
            echo 'Invalid request.';
        }
    }

    public function emptyCart() {
        $this->cart = new Cart();
        echo 'Cart emptied.';
    }
}


