<?php

/**
 * Function to display user reviews with options for filtering and pagination.
 *
 * @param string $review_id The ID of the item being reviewed.  Used for display.
 * @param array $reviews An array of review objects (see example below).
 * @param int $limit  The maximum number of reviews to display per page (default: 10).
 * @param int $page   The page number to display (default: 1).
 * @param array $filters An associative array of filters to apply to the reviews.
 *                       Example: ['author' => 'John Doe', 'rating' => 5]
 * @return string The HTML output of the reviews.
 */
function display_reviews(string $review_id, array $reviews, int $limit = 10, int $page = 1, array $filters = [])
{
  // Input validation (basic)
  if (empty($reviews)) {
    return "<p>No reviews found.</p>";
  }

  if ($page <= 0) {
    $page = 1;
  }

  $offset = ($page - 1) * $limit;

  // Filter the reviews
  $filtered_reviews = array_filter($reviews, function ($review) use ($filters) {
    // Apply all filters
    foreach ($filters as $filter_key => $filter_value) {
      if (isset($review[$filter_key])) {
        if ($review[$filter_key] !== $filter_value) {
          return false;
        }
      } else {
        // If a filter key doesn't exist in the review, exclude it
        return false;
      }
    }
    return true;
  });


  // Paginate the reviews
  $total_reviews = count($filtered_reviews);
  $pages = ceil($total_reviews / $limit);

  // Build the HTML output
  $html = "<div class='reviews'>";

  if ($total_reviews > 0) {
    foreach ($filtered_reviews as $review) {
      $html .= "<div class='review'>";
      $html .= "<p class='review-author'>By: " . htmlspecialchars($review['author']) . "</p>";
      $html .= "<p class='review-rating'>Rating: " . htmlspecialchars($review['rating']) . "</p>";
      $html .= "<p class='review-text'>" . nl2br(htmlspecialchars($review['text'])) . "</p>"; // nl2br for line breaks
      $html .= "</div>";
    }
  }

  // Pagination controls
  if ($pages > 1) {
    $html .= "<div class='pagination'>";
    $html .= "<ul class='pagination-links'>";
    for ($i = 1; $i <= $pages; $i++) {
      $class = ($i === $page) ? 'active' : '';
      $html .= "<li class='$class'><a href='#' data-page='$page' data-total-pages='$pages'>$i</a></li>";
    }
    $html .= "</ul>";
    $html .= "<ul class='pagination-links'>";
    $html .= "<li class='disabled'><a href='#' data-page='1' data-total-pages='$pages'>&laquo;</a></li>";
    $html .= "<li class='disabled'><a href='#' data-page='1' data-total-pages='$pages'>&lt;</a></li>";
    $html .= "<li class='disabled'><a href='#' data-page='1' data-total-pages='$pages'>&lt;&lt;</a></li>";
    $html .= "</ul>";
    $html .= "<ul class='pagination-links'>";
