<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <title>Search Bar</title>
    <style>
        .search-container {
            max-width: 600px;
            margin: 50px auto;
            padding: 20px;
        }

        .search-bar {
            width: 70%;
            padding: 10px;
            font-size: 16px;
            border-radius: 4px;
            border: 1px solid #ddd;
        }

        .search-button {
            padding: 10px 20px;
            background-color: #4CAF50;
            color: white;
            border: none;
            border-radius: 4px;
            cursor: pointer;
        }

        .results {
            margin-top: 20px;
            padding: 10px;
            background-color: #f8f9fa;
            min-height: 100px;
        }
    </style>
</head>
<body>
    <?php
    $searchQuery = "";
    $results = array();

    // Sample data for demonstration
    $data = array(
        "Apple",
        "Banana",
        "Cherry",
        "Date",
        "Elderberry",
        "Fig",
        "Grape",
        "Honeydew Melon"
    );

    if ($_SERVER["REQUEST_METHOD"] == "GET") {
        // Check if search term is provided
        if (isset($_GET['search'])) {
            $searchQuery = $_GET['search'];

            // Search through the data array
            foreach ($data as $item) {
                if (stripos($item, $searchQuery) !== false) {
                    array_push($results, $item);
                }
            }
        } else {
            // If no search term provided, show all items
            $results = $data;
        }
    }

    // Display error message if no search query is provided
    if (empty($searchQuery)) {
        echo "<div style='color: red; margin-bottom: 10px;'>Please enter a search term.</div>";
    }
    ?>

    <div class="search-container">
        <form action="<?php echo htmlspecialchars($_SERVER["PHP_SELF"]); ?>" method="GET">
            <input type="text" name="search" class="search-bar" placeholder="Search..." value="<?php echo $searchQuery; ?>">
            <button type="submit" class="search-button">Search</button>
        </form>

        <div class="results">
            <?php if (!empty($results)) : ?>
                <h3>Results:</h3>
                <ul>
                    <?php foreach ($results as $result) : ?>
                        <li><?php echo $result; ?></li>
                    <?php endforeach; ?>
                </ul>
            <?php endif; ?>
        </div>
    </div>
</body>
</html>
