    if (empty($email)) {
        $_SESSION['message'] = "Please enter your email address!";
        header("Location: forgot-password.php");
        exit();
    }

    if (!filter_var($email, FILTER_VALIDATE_EMAIL)) {
        $_SESSION['message'] = "Please enter a valid email address!";
        header("Location: forgot-password.php");
        exit();
