<?php
// database connection
$host = 'localhost';
$dbname = 'mydatabase';
$username = 'root';
$password = '';

try {
    $conn = new PDO("mysql:host=$host;dbname=$dbname", $username, $password);
    $conn->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);
} catch (PDOException $e) {
    die("Connection failed: " . $e->getMessage());
}

// Step 1: Show reset password form
if ($_SERVER["REQUEST_METHOD"] == "GET" && isset($_GET['email'])) {
    // Check if email exists in database
    $email = $_GET['email'];
    $stmt = $conn->prepare("SELECT id FROM users WHERE email = ?");
    $stmt->execute([$email]);
    $user = $stmt->fetch();
    
    if ($user) {
        // Generate reset token and expiration time
        $resetToken = bin2hex(random_bytes(16));
        $expirationTime = date('Y-m-d H:i:s', strtotime('+1 hour'));
        
        // Store token in database
        $stmt = $conn->prepare("INSERT INTO password_resets (user_id, token, expires_at) VALUES (?, ?, ?)");
        $stmt->execute([$user['id'], $resetToken, $expirationTime]);
        
        // Send reset email
        $to = $email;
        $subject = "Password Reset Request";
        $message = "Please click the following link to reset your password: http://example.com/reset-password.php?token=$resetToken";
        $headers = "From: no-reply@example.com";
        
        mail($to, $subject, $message, $headers);
        
        echo "A password reset link has been sent to your email.";
    } else {
        echo "Email not found in our records.";
    }
}

// Step 2: Reset password page
if ($_SERVER["REQUEST_METHOD"] == "GET" && isset($_GET['token'])) {
    // Validate token and display reset form
    $token = $_GET['token'];
    $stmt = $conn->prepare("SELECT * FROM password_resets WHERE token = ? AND expires_at > NOW()");
    $stmt->execute([$token]);
    $reset = $stmt->fetch();
    
    if ($reset) {
        // Show reset form
        ?>
        <form action="reset-password.php" method="POST">
            <input type="hidden" name="token" value="<?php echo $token; ?>">
            <input type="password" name="new_password" placeholder="New Password" required>
            <button type="submit">Reset Password</button>
        </form>
        <?php
    } else {
        echo "Invalid or expired reset token.";
    }
}

// Step 3: Process password reset
if ($_SERVER["REQUEST_METHOD"] == "POST") {
    $token = $_POST['token'];
    $newPassword = $_POST['new_password'];
    
    // Validate token and update password
    $stmt = $conn->prepare("SELECT * FROM password_resets WHERE token = ? AND expires_at > NOW()");
    $stmt->execute([$token]);
    $reset = $stmt->fetch();
    
    if ($reset) {
        // Update user's password
        $hashedPassword = password_hash($newPassword, PASSWORD_DEFAULT);
        $stmt = $conn->prepare("UPDATE users SET password = ? WHERE id = ?");
        $stmt->execute([$hashedPassword, $reset['user_id']]);
        
        // Delete used token
        $stmt = $conn->prepare("DELETE FROM password_resets WHERE token = ?");
        $stmt->execute([$token]);
        
        echo "Your password has been reset successfully!";
    } else {
        echo "Invalid or expired reset token.";
    }
}

// Close database connection
$conn = null;
?>


