    $check_token_query = "
        SELECT 
            pr.id, 
            u.id AS user_id,
            pr.expires 
        FROM 
            password_reset pr 
        JOIN 
            users u ON pr.user_id = u.id 
        WHERE 
            pr.token='$token'
    ";
    
    $result = mysqli_query($conn, $check_token_query);
    
    if (mysqli_num_rows($result) > 0) {
        $row = mysqli_fetch_assoc($result);
        
        // Check if token is expired
        if ($row['expires'] < date('Y-m-d H:i:s')) {
