<?php
session_start();
require_once 'database.php';

if ($_SERVER["REQUEST_METHOD"] == "GET") {

    if (!isset($_GET['token']) || !isset($_GET['id'])) {
        die("Invalid request.");
    }
    
    $token = $_GET['token'];
    $user_id = $_GET['id'];
    
    try {
        // Check token validity
        $stmt = $conn->prepare("
            SELECT 
                rp.token,
                u.id
            FROM reset_passwords AS rp
            INNER JOIN users AS u ON rp.user_id = u.id
            WHERE rp.token = SHA1(?) AND u.id = ?
            AND rp.expires > NOW()
        ");
        $stmt->execute([$token, $user_id]);
        
        if (!$stmt->rowCount()) {
            die("Invalid or expired reset link.");
        }
        
    } catch(PDOException $e) {
        die("An error occurred. Please try again later.");
    }
    
}

if ($_SERVER["REQUEST_METHOD"] == "POST") {

    // Check if password fields are set
    if (!isset($_POST['password']) || !isset($_POST['confirm'])) {
        die("Please fill in all required fields.");
    }
    
    $new_password = $_POST['password'];
    $confirm_password = $_POST['confirm'];
    
    if ($new_password !== $confirm_password) {
        die("Passwords do not match. Please try again.");
    }
    
    if (strlen($new_password) < 8 || strlen($new_password) > 20) {
        die("Password must be between 8 and 20 characters long.");
    }
    
    // Validate token
    $token = $_GET['token'];
    $user_id = $_GET['id'];
    
    try {
        $stmt = $conn->prepare("
            SELECT 
                rp.token,
                u.id
            FROM reset_passwords AS rp
            INNER JOIN users AS u ON rp.user_id = u.id
            WHERE rp.token = SHA1(?) AND u.id = ?
            AND rp.expires > NOW()
        ");
        $stmt->execute([$token, $user_id]);
        
        if (!$stmt->rowCount()) {
            die("Invalid or expired reset link.");
        }
        
        // Update the password
        $new_password_hashed = password_hash($new_password, PASSWORD_DEFAULT);
        $stmt = $conn->prepare("
            UPDATE users 
            SET password = ?
            WHERE id = ?
        ");
        $stmt->execute([$new_password_hashed, $user_id]);
        
        // Clear reset token
        $stmt = $conn->prepare("
            DELETE FROM reset_passwords 
            WHERE user_id = ?
        ");
        $stmt->execute([$user_id]);
        
        header("Location: login.php?msg=Password updated successfully.");
        exit();
    } catch(PDOException $e) {
        die("An error occurred. Please try again later.");
    }
    
}
