        function generatePassword() {
            $chars = "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789";
            $length = strlen($chars);
            $newpass = '';
            for ($i = 0; $i < 8; $i++) {
                $newpass .= $chars[rand(0, $length - 1)];
            }
            return $newpass;
        }

        $newpass = generatePassword();

        // Update the password in database
