// Function to generate temporary password
function generate_temp_password() {
    $chars = "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789!@#$%^&*";
    $length = strlen($chars);
    $temp_pass = "";
    for ($i = 0; $i < 8; $i++) {
        $random_index = rand(0, $length - 1);
        $temp_pass .= $chars[$random_index];
    }
    return $temp_pass;
}
