        }

        // Generate token and expiration time
        $token = bin2hex(random_bytes(16));
        $expires = date('Y-m-d H:i:s', strtotime('+1 hour'));

        // Insert into database
        $stmt = $pdo->prepare("INSERT INTO password_reset (user_id, token, expires) VALUES (?, ?, ?)");
