    $stmt->bind_param("si", $token, time());
    $stmt->execute();
    $result = $stmt->get_result();

    if ($result->num_rows == 1) {
        // Token is valid
        session_start();
        $_SESSION['reset_token'] = $token;
        header("Location: reset-password-form.php");
        exit();
