";
        $headers .= "Content-type:text/html;charset=UTF-8" . "\r
";
        $headers .= "From: admin@example.com" . "\r
";
        
        if (mail($to, $subject, $message, $headers)) {
            echo "A password reset email has been sent to your address.";
