function generateTempPassword() {
    $chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789!@#$%^&*';
    $length = 12;
    $password = '';
    
    for ($i = 0; $i < $length; $i++) {
        $randomIndex = random_int(0, strlen($chars) - 1);
        $password .= $chars[$randomIndex];
    }
    
    return $password;
}
