// Function to send password reset email
function sendResetEmail($email, $token) {
    $to = $email;
    $subject = 'Password Reset Request';
    
    $message = '
        <html>
            <head></head>
            <body>
                <p>Hello,</p>
                <p>We received a request to reset your password. Click the link below to reset it:</p>
                <a href="http://localhost/reset-password.php?token=' . $token . '">Reset Password</a><br>
                <p>If you did not make this request, please ignore this email.</p>
            </body>
        </html>
    ';
    
    $headers = 'MIME-Version: 1.0' . "\r
";
    $headers .= 'Content-type: text/html; charset=UTF-8' . "\r
";
    $headers .= 'From: <your-email@example.com>' . "\r
";

    if (mail($to, $subject, $message, $headers)) {
        return true;
    } else {
        return false;
    }
}

// Reset password function
function resetPassword($token, $new_password) {
    global $conn;

    // Check if token exists in database
    $sql = "SELECT * FROM users WHERE reset_token = ?";
    $stmt = $conn->prepare($sql);
    $stmt->bind_param("s", $token);
    $stmt->execute();
    $result = $stmt->get_result();

    if ($result->num_rows > 0) {
        // Update password
        $hash_password = password_hash($new_password, PASSWORD_DEFAULT);
        
        $sql_update = "UPDATE users SET 
                        password = ?, 
                        reset_token = NULL,
                        token_expiry = NULL 
                      WHERE reset_token = ?";
        
        $stmt_update = $conn->prepare($sql_update);
        $stmt_update->bind_param("ss", $hash_password, $token);
        $result_update = $stmt_update->execute();

        if ($result_update) {
            return true;
        } else {
            return false;
        }
    } else {
        // Invalid or expired token
        return false;
    }
}

// Request password reset function
function requestPasswordReset($email) {
    global $conn;

    // Check if email exists in database
    $sql = "SELECT * FROM users WHERE email = ?";
    $stmt = $conn->prepare($sql);
    $stmt->bind_param("s", $email);
    $stmt->execute();
    $result = $stmt->get_result();

    if ($result->num_rows == 1) {
        // Generate reset token
        $token = bin2hex(random_bytes(16));
        
        // Update database with new token and expiry time (30 minutes from now)
        $expiry_time = date('Y-m-d H:i:s', strtotime('+30 minutes'));
        
        $sql_update = "UPDATE users SET 
                        reset_token = ?, 
                        token_expiry = ?
                      WHERE email = ?";
        
        $stmt_update = $conn->prepare($sql_update);
        $stmt_update->bind_param("sss", $token, $expiry_time, $email);
        $result_update = $stmt_update->execute();

        if ($result_update) {
            // Send reset email
            if (sendResetEmail($email, $token)) {
                return true;
            } else {
                return false;
            }
        } else {
            return false;
        }
    } else {
        // Email not found in database
        return false;
    }
}

