function validateToken($token) {
    global $conn;

    // Check if token exists and is not expired
    $sql = "SELECT * FROM password_reset 
            WHERE token = ? 
            AND expires > NOW() 
            AND used = 0";
    $stmt = $conn->prepare($sql);
    $stmt->bind_param("s", $token);
    $stmt->execute();
    $result = $stmt->get_result();

    if ($result->num_rows == 1) {
        return true;
    } else {
        return false;
    }
}

