        $stmt->execute([$token]);
        $result = $stmt->fetch(PDO::FETCH_ASSOC);

        if (!$result) {
            die("Invalid or expired reset link. Please request a new one.");
        }

        // Validate password
        $new_password = $_POST['new_password'];
