<?php
// Configuration
session_start();

// Database connection (replace with your own credentials)
$servername = "localhost";
$username = "username";
$password = "password";
$dbname = "myDB";

// Create database connection
$conn = new mysqli($servername, $username, $password, $dbname);
if ($conn->connect_error) {
    die("Connection failed: " . $conn->connect_error);
}

// Function to get products from database
function getProducts() {
    global $conn;
    $sql = "SELECT * FROM products";
    $result = $conn->query($sql);
    return $result;
}

// Function to add product to cart
function addToCart($productId) {
    global $_SESSION;
    if (!isset($_SESSION['cart'])) {
        $_SESSION['cart'] = array();
    }
    if (!array_key_exists($productId, $_SESSION['cart'])) {
        $_SESSION['cart'][$productId] = 1;
    } else {
        $_SESSION['cart'][$productId]++;
    }
}

// Function to update cart
function updateCart($productId, $quantity) {
    global $_SESSION;
    if ($quantity > 0) {
        $_SESSION['cart'][$productId] = $quantity;
    } else {
        unset($_SESSION['cart'][$productId]);
    }
}

// Function to get product details from database
function getProductDetails($productId) {
    global $conn;
    $sql = "SELECT * FROM products WHERE id = ?";
    $stmt = $conn->prepare($sql);
    $stmt->bind_param("i", $productId);
    $stmt->execute();
    $result = $stmt->get_result();
    return $result->fetch_assoc();
}

// Function to save order
function saveOrder($userId, $cartItems) {
    global $conn;
    // Save order details
    $sql = "INSERT INTO orders (user_id, total_amount) VALUES (?, ?)";
    $stmt = $conn->prepare($sql);
    $totalAmount = 0;
    foreach ($cartItems as $productId => $quantity) {
        $product = getProductDetails($productId);
        $totalAmount += $product['price'] * $quantity;
    }
    $stmt->bind_param("id", $userId, $totalAmount);
    $stmt->execute();
    $orderId = $conn->insert_id;

    // Save order items
    foreach ($cartItems as $productId => $quantity) {
        $sql = "INSERT INTO order_items (order_id, product_id, quantity) VALUES (?, ?, ?)";
        $stmt = $conn->prepare($sql);
        $stmt->bind_param("iii", $orderId, $productId, $quantity);
        $stmt->execute();
    }
    return $orderId;
}

// Function to process payment
function processPayment($amount) {
    // Implement your payment gateway logic here
    // For this example, we'll just simulate the payment
    return true; // Return true if payment is successful
}
?>


