$id = isset($_GET['id']) ? (int)$_GET['id'] : 0;

switch ($action) {
    case 'remove':
        if (array_key_exists($id, $cart)) {
            unset($cart[$id]);
        }
        break;
        
    case 'update':
        $quantity = isset($_GET['quantity']) ? (int)$_GET['quantity'] : 1;
        if ($quantity < 1) {
            $quantity = 1;
        }
        if (array_key_exists($id, $cart)) {
            $cart[$id]['quantity'] = $quantity;
        }
        break;
}

header("Location: cart.php");
