// Product class
class Product {
    private $conn;

    public function __construct($db_connection) {
        $this->conn = $db_connection;
    }

    // Get all products
    public function getProducts() {
        try {
            $stmt = $this->conn->query("SELECT * FROM products");
            return $stmt->fetchAll(PDO::FETCH_ASSOC);
        } catch (PDOException $e) {
            echo "Error: " . $e->getMessage();
        }
    }

    // Get product by ID
    public function getProductById($id) {
        try {
            $stmt = $this->conn->prepare("SELECT * FROM products WHERE id = ?");
            $stmt->execute([$id]);
            return $stmt->fetch(PDO::FETCH_ASSOC);
        } catch (PDOException $e) {
            echo "Error: " . $e->getMessage();
        }
    }

    // Add product
    public function addProduct($data) {
        try {
            $stmt = $this->conn->prepare("INSERT INTO products SET 
                name = ?, 
                price = ?, 
                description = ?, 
                quantity = ?, 
                category = ?");
            $stmt->execute([
                $data['name'],
                $data['price'],
                $data['description'],
                $data['quantity'],
                $data['category']
            ]);
            return true;
        } catch (PDOException $e) {
            echo "Error: " . $e->getMessage();
            return false;
        }
    }

    // Update product
    public function updateProduct($id, $data) {
        try {
            $stmt = $this->conn->prepare("UPDATE products SET 
                name = ?, 
                price = ?, 
                description = ?, 
                quantity = ?, 
                category = ? WHERE id = ?");
            $stmt->execute([
                $data['name'],
                $data['price'],
                $data['description'],
                $data['quantity'],
                $data['category'],
                $id
            ]);
            return true;
        } catch (PDOException $e) {
            echo "Error: " . $e->getMessage();
            return false;
        }
    }

    // Delete product
    public function deleteProduct($id) {
        try {
            $stmt = $this->conn->prepare("DELETE FROM products WHERE id = ?");
            $stmt->execute([$id]);
            return true;
        } catch (PDOException $e) {
            echo "Error: " . $e->getMessage();
            return false;
        }
    }
}

// Order class
class Order {
    private $conn;

    public function __construct($db_connection) {
        $this->conn = $db_connection;
    }

    // Get all orders
    public function getOrders() {
        try {
            $stmt = $this->conn->query("SELECT * FROM orders");
            return $stmt->fetchAll(PDO::FETCH_ASSOC);
        } catch (PDOException $e) {
            echo "Error: " . $e->getMessage();
        }
    }

    // Get order by ID
    public function getOrderById($id) {
        try {
            $stmt = $this->conn->prepare("SELECT * FROM orders WHERE id = ?");
            $stmt->execute([$id]);
            return $stmt->fetch(PDO::FETCH_ASSOC);
        } catch (PDOException $e) {
            echo "Error: " . $e->getMessage();
        }
    }

    // Process order
    public function processOrder($data, $product_id, $user_id) {
        try {
            // Check if product is in stock
            $product = new Product($this->conn);
            $product_details = $product->getProductById($product_id);
            
            if ($product_details['quantity'] < $data['quantity']) {
                return false; // Not enough stock
            }

            // Update product quantity
            $new_quantity = $product_details['quantity'] - $data['quantity'];
            $update_stmt = $this->conn->prepare("UPDATE products SET quantity = ? WHERE id = ?");
            $update_stmt->execute([$new_quantity, $product_id]);

            // Insert order
            $order_stmt = $this->conn->prepare("INSERT INTO orders SET 
                user_id = ?, 
                product_id = ?, 
                quantity = ?, 
                order_date = NOW(), 
                status = 'pending', 
                payment_method = ?");
            $order_stmt->execute([
                $user_id,
                $product_id,
                $data['quantity'],
                $data['payment_method']
            ]);
            
            return true;
        } catch (PDOException $e) {
            echo "Error: " . $e->getMessage();
            return false;
        }
    }

    // Get order history for user
    public function getOrderHistory($user_id) {
        try {
            $stmt = $this->conn->prepare("SELECT * FROM orders WHERE user_id = ?");
            $stmt->execute([$user_id]);
            return $stmt->fetchAll(PDO::FETCH_ASSOC);
        } catch (PDOException $e) {
            echo "Error: " . $e->getMessage();
        }
    }
}

// User class
class User {
    private $conn;

    public function __construct($db_connection) {
        $this->conn = $db_connection;
    }

    // Register user
    public function register($data) {
        try {
            // Check if username exists
            $check_stmt = $this->conn->prepare("SELECT * FROM users WHERE username = ?");
            $check_stmt->execute([$data['username']]);
            
            if ($check_stmt->rowCount() > 0) {
                return false; // Username already exists
            }

            // Hash password
            $hashed_password = password_hash($data['password'], PASSWORD_DEFAULT);

            // Insert user
            $stmt = $this->conn->prepare("INSERT INTO users SET 
                username = ?, 
                email = ?, 
                password = ?, 
                address = ?, 
                phone = ?, 
                created_at = NOW()");
            $stmt->execute([
                $data['username'],
                $data['email'],
                $hashed_password,
                $data['address'],
                $data['phone']
            ]);
            
            return true;
        } catch (PDOException $e) {
            echo "Error: " . $e->getMessage();
            return false;
        }
    }

    // Login user
    public function login($username, $password) {
        try {
            $stmt = $this->conn->prepare("SELECT * FROM users WHERE username = ?");
            $stmt->execute([$username]);
            $user = $stmt->fetch(PDO::FETCH_ASSOC);

            if (!$user || !password_verify($password, $user['password'])) {
                return false;
            }

            // Start session
            session_start();
            $_SESSION['user_id'] = $user['id'];
            $_SESSION['username'] = $user['username'];
            
            return true;
        } catch (PDOException $e) {
            echo "Error: " . $e->getMessage();
            return false;
        }
    }

    // Get user by ID
    public function getUserById($id) {
        try {
            $stmt = $this->conn->prepare("SELECT * FROM users WHERE id = ?");
            $stmt->execute([$id]);
            return $stmt->fetch(PDO::FETCH_ASSOC);
        } catch (PDOException $e) {
            echo "Error: " . $e->getMessage();
        }
    }

    // Update user
    public function updateUser($id, $data) {
        try {
            $stmt = $this->conn->prepare("UPDATE users SET 
                username = ?, 
                email = ?, 
                address = ?, 
                phone = ? WHERE id = ?");
            $stmt->execute([
                $data['username'],
                $data['email'],
                $data['address'],
                $data['phone'],
                $id
            ]);
            
            return true;
        } catch (PDOException $e) {
            echo "Error: " . $e->getMessage();
            return false;
        }
    }

    // Delete user
    public function deleteUser($id) {
        try {
            $stmt = $this->conn->prepare("DELETE FROM users WHERE id = ?");
            $stmt->execute([$id]);
            
            session_unset();
            session_destroy();
            
            return true;
        } catch (PDOException $e) {
            echo "Error: " . $e->getMessage();
            return false;
        }
    }
}

// Create tables if not exists
try {
