// Function to get products from database
function get_products() {
    global $conn;
    $sql = "SELECT * FROM products";
    $result = $conn->query($sql);
    
    if ($result->num_rows > 0) {
        while ($row = $result->fetch_assoc()) {
            echo "<div class='product'>";
            echo "<h3>" . $row['name'] . "</h3>";
            echo "<p>Price: $" . number_format($row['price'], 2) . "</p>";
            echo "<form action='add_to_cart.php' method='post'>";
            echo "<input type='hidden' name='product_id' value='" . $row['id'] . "'>";
            echo "<input type='number' name='quantity' min='1' max='" . $row['quantity'] . "' value='1'>";
            echo "<button type='submit'>Add to Cart</button>";
            echo "</form>";
            echo "</div>";
        }
    } else {
        echo "No products available.";
    }
}

// Function to add items to cart
function add_to_cart($product_id, $quantity) {
    global $conn;
    
    if (isset($_SESSION['cart'])) {
        $cart = $_SESSION['cart'];
        
        if (array_key_exists($product_id, $cart)) {
            $cart[$product_id]['quantity'] += $quantity;
        } else {
            $sql = "SELECT * FROM products WHERE id = ?";
            $stmt = $conn->prepare($sql);
            $stmt->bind_param("i", $product_id);
            $stmt->execute();
            
            $result = $stmt->get_result();
            $product = $result->fetch_assoc();
            
            $cart[$product_id] = array(
                'name' => $product['name'],
                'price' => $product['price'],
                'quantity' => $quantity
            );
        }
    } else {
        $sql = "SELECT * FROM products WHERE id = ?";
        $stmt = $conn->prepare($sql);
        $stmt->bind_param("i", $product_id);
        $stmt->execute();
        
        $result = $stmt->get_result();
        $product = $result->fetch_assoc();
        
        $_SESSION['cart'] = array(
            $product_id => array(
                'name' => $product['name'],
                'price' => $product['price'],
                'quantity' => $quantity
            )
        );
    }
    
    $_SESSION['cart'] = $cart;
}

// Function to process checkout
function process_checkout() {
    global $conn;
    
    if (!isset($_SESSION['user_id'])) {
        header("Location: login.php");
        exit();
    }
    
    $total_price = 0;
    $order_items = array();
    
    foreach ($_SESSION['cart'] as $product_id => $item) {
        $total_price += ($item['price'] * $item['quantity']);
        
        $order_items[] = array(
            'product_id' => $product_id,
            'quantity' => $item['quantity'],
            'user_id' => $_SESSION['user_id']
        );
    }
    
    // Insert order into database
    $sql = "INSERT INTO orders (total_price, user_id) VALUES (?, ?)";
    $stmt = $conn->prepare($sql);
    $stmt->bind_param("di", $total_price, $_SESSION['user_id']);
    $stmt->execute();
    
    $order_id = $conn->insert_id;
    
    // Insert order items into database
    foreach ($order_items as $item) {
        $sql = "INSERT INTO order_items (order_id, product_id, quantity, user_id) 
                VALUES (?, ?, ?, ?)";
        $stmt = $conn->prepare($sql);
        $stmt->bind_param("iiii", $order_id, $item['product_id'], $item['quantity'], $item['user_id']);
        $stmt->execute();
    }
    
    // Update product quantities
    foreach ($_SESSION['cart'] as $product_id => $item) {
        $sql = "UPDATE products SET quantity = quantity - ? WHERE id = ?";
        $stmt = $conn->prepare($sql);
        $stmt->bind_param("ii", $item['quantity'], $product_id);
        $stmt->execute();
    }
    
    // Clear cart
    unset($_SESSION['cart']);
    
    return true;
}
?>


