// Database connection
$servername = "localhost";
$username = "username";
$password = "password";
$dbname = "myDB";

$conn = mysqli_connect($servername, $username, $password, $dbname);
if (!$conn) {
    die("Connection failed: " . mysqli_connect_error());
}

// User authentication functions

function registerUser($username, $email, $password, $address) {
    global $conn;
    $hashed_password = password_hash($password, PASSWORD_DEFAULT);
    
    $sql = "INSERT INTO users (username, email, password, address) VALUES ('$username', '$email', '$hashed_password', '$address')";
    if (mysqli_query($conn, $sql)) {
        return true;
    } else {
        return false;
    }
}

function loginUser($email, $password) {
    global $conn;
    $sql = "SELECT * FROM users WHERE email='$email'";
    $result = mysqli_query($conn, $sql);
    
    if (mysqli_num_rows($result) == 1) {
        $user = mysqli_fetch_assoc($result);
        if (password_verify($password, $user['password'])) {
            $_SESSION['user'] = array(
                'id' => $user['id'],
                'username' => $user['username'],
                'email' => $user['email']
            );
            return true;
        }
    }
    return false;
}

// Product functions

function getProducts() {
    global $conn;
    $sql = "SELECT * FROM products";
    $result = mysqli_query($conn, $sql);
    $products = array();
    
    while ($row = mysqli_fetch_assoc($result)) {
        $products[] = $row;
    }
    
    return $products;
}

function getProductById($id) {
    global $conn;
    $sql = "SELECT * FROM products WHERE id='$id'";
    $result = mysqli_query($conn, $sql);
    return mysqli_fetch_assoc($result);
}

// Cart functions

function addToCart($productId, $quantity) {
    if (!isset($_SESSION['cart'])) {
        $_SESSION['cart'] = array();
    }
    
    if (isset($_SESSION['cart'][$productId])) {
        $_SESSION['cart'][$productId]['quantity'] += $quantity;
    } else {
        $product = getProductById($productId);
        $_SESSION['cart'][$productId] = array(
            'id' => $product['id'],
            'name' => $product['name'],
            'price' => $product['price'],
            'quantity' => $quantity
        );
    }
}

function getCartTotal() {
    $total = 0;
    foreach ($_SESSION['cart'] as $item) {
        $total += ($item['price'] * $item['quantity']);
    }
    return $total;
}

// Order functions

function placeOrder() {
    if (!isset($_SESSION['user'])) {
        header("Location: login.php");
        exit();
    }
    
    $userId = $_SESSION['user']['id'];
    $cart = $_SESSION['cart'];
    $totalPrice = getCartTotal();
    
    foreach ($cart as $item) {
        $sql = "INSERT INTO orders (user_id, product_id, quantity, total_price) VALUES ('$userId', '{$item['id']}', '{$item['quantity']}', '{$item['price'] * $item['quantity']}')";
        mysqli_query($conn, $sql);
    }
    
    // Clear cart
    unset($_SESSION['cart']);
}

// Database tables creation

function createTables() {
    global $conn;
    
    // Create users table
    $sql = "CREATE TABLE IF NOT EXISTS users (
        id INT AUTO_INCREMENT PRIMARY KEY,
        username VARCHAR(255),
        email VARCHAR(255),
        password VARCHAR(255),
        address TEXT
    )";
    mysqli_query($conn, $sql);
    
    // Create products table
    $sql = "CREATE TABLE IF NOT EXISTS products (
        id INT AUTO_INCREMENT PRIMARY KEY,
        name VARCHAR(255),
        description TEXT,
        price DECIMAL(10, 2),
        stock INT
    )";
    mysqli_query($conn, $sql);
    
    // Create orders table
    $sql = "CREATE TABLE IF NOT EXISTS orders (
        id INT AUTO_INCREMENT PRIMARY KEY,
        user_id INT,
        product_id INT,
        quantity INT,
        total_price DECIMAL(10, 2),
        date DATETIME DEFAULT CURRENT_TIMESTAMP,
        FOREIGN KEY (user_id) REFERENCES users(id),
        FOREIGN KEY (product_id) REFERENCES products(id)
    )";
    mysqli_query($conn, $sql);
}

