    }

    // Insert order details into database
    foreach ($_SESSION['cart'] as $item) {
        $sqlDetails = "INSERT INTO order_details (order_id, product_id, quantity, price) VALUES ('$orderId', '$item[id]', '$item[quantity]', '$item[price]')";
