// Function to get products
function getProducts() {
    global $conn;
    $sql = "SELECT * FROM products";
    $result = $conn->query($sql);
    
    if ($result->num_rows > 0) {
        while($row = $result->fetch_assoc()) {
            echo "<div class='product'>";
            echo "<h3>".$row["name"]."</h3>";
            echo "<p>Price: $".$row["price"]."</p>";
            echo "<button onclick='addToCart(".$row["id"].")'>Add to Cart</button>";
            echo "</div>";
        }
    } else {
        echo "No products found";
    }
}

// Function to add item to cart
function addToCart($productId) {
    global $conn;
    
    // Start session if not started
    if(!isset($_SESSION)) {
        session_start();
    }
    
    // Check if product exists in database
    $sql = "SELECT * FROM products WHERE id=?";
    $stmt = $conn->prepare($sql);
    $stmt->bind_param("i", $productId);
    $stmt->execute();
    $result = $stmt->get_result();
    
    if ($result->num_rows > 0) {
        // Add to cart
        $product = $result->fetch_assoc();
        
        if (isset($_SESSION['cart'][$productId])) {
            $_SESSION['cart'][$productId]['quantity']++;
        } else {
            $_SESSION['cart'][$productId] = array(
                "name" => $product["name"],
                "price" => $product["price"],
                "quantity" => 1
            );
        }
        
        echo "Item added to cart";
    } else {
        echo "Product not found";
    }
}

// Function to calculate cart total
function getCartTotal() {
    global $conn;
    
    if (!isset($_SESSION)) {
        session_start();
    }
    
    $total = 0;
    foreach ($_SESSION['cart'] as $item) {
        $total += ($item['price'] * $item['quantity']);
    }
    
    return $total;
}

// Function to place order
function placeOrder() {
    global $conn;
    
    if (!isset($_SESSION)) {
        session_start();
    }
    
    // Get user ID (assuming you have a logged-in user)
    $userId = 1; // Replace with actual user ID
    
    $cart = $_SESSION['cart'];
    $totalAmount = getCartTotal();
    
    // Insert order into orders table
    $sql = "INSERT INTO orders (user_id, total_amount, created_at, updated_at) 
            VALUES (?, ?, NOW(), NOW())";
    $stmt = $conn->prepare($sql);
    $stmt->bind_param("id", $userId, $totalAmount);
    
    if ($stmt->execute()) {
        // Get order ID
        $orderId = $conn->insert_id;
        
        // Insert order items into order_items table
        foreach ($cart as $productId => $item) {
            $sql = "INSERT INTO order_items (order_id, product_id, quantity, price)
                    VALUES (?, ?, ?, ?)";
            $stmt = $conn->prepare($sql);
            $stmt->bind_param("iiid", $orderId, $productId, $item['quantity'], $item['price']);
            $stmt->execute();
        }
        
        // Clear cart
        unset($_SESSION['cart']);
        
        return true;
    } else {
        return false;
    }
}

// Function to generate payment receipt
function generatePaymentReceipt($order_id) {
    global $conn;
    
    $sql = "SELECT * FROM orders WHERE id=? AND user_id=?";
    $stmt = $conn->prepare($sql);
    $stmt->bind_param("ii", $order_id, $_SESSION['user_id']);
    $stmt->execute();
    $result = $stmt->get_result();
    
    if ($result->num_rows > 0) {
        $order = $result->fetch_assoc();
        
        // Get order items
        $sql = "SELECT * FROM order_items WHERE order_id=?";
        $stmt = $conn->prepare($sql);
        $stmt->bind_param("i", $order_id);
        $stmt->execute();
        $itemsResult = $stmt->get_result();
        
        echo "<div class='receipt'>";
        echo "<h2>Payment Receipt</h2>";
        echo "<p>Order ID: ".$order['id']."</p>";
        echo "<p>Total Amount: $".$order['total_amount']."</p>";
        echo "<h3>Items:</h3>";
        
        while($item = $itemsResult->fetch_assoc()) {
            echo "<div class='item'>";
            echo "<p>".$item['name']." x ".$item['quantity']."</p>";
            echo "</div>";
        }
        
        echo "</div>";
    } else {
        echo "Order not found";
    }
}

