// Get products from database
function getProducts() {
    global $conn;
    $stmt = $conn->prepare("SELECT * FROM products");
    $stmt->execute();
    return $stmt->fetchAll(PDO::FETCH_ASSOC);
}

// Add product to cart
function addToCart($productId, $quantity) {
    if (!isset($_SESSION)) {
        session_start();
    }
    
    if (!isset($_SESSION['cart'])) {
        $_SESSION['cart'] = array();
    }

    if (!array_key_exists($productId, $_SESSION['cart'])) {
        $_SESSION['cart'][$productId] = 0;
    }

    $_SESSION['cart'][$productId] += $quantity;
}

// Update cart quantity
function updateCart($productId, $quantity) {
    if (isset($_SESSION['cart'][$productId])) {
        $_SESSION['cart'][$productId] = $quantity;
    }
}

// Remove product from cart
function removeProduct($productId) {
    if (isset($_SESSION['cart'][$productId])) {
        unset($_SESSION['cart'][$productId']);
    }
}

// Calculate total price
function calculateTotal() {
    $total = 0;
    foreach ($_SESSION['cart'] as $productId => $quantity) {
        $stmt = $conn->prepare("SELECT price FROM products WHERE id = ?");
        $stmt->execute([$productId]);
        $price = $stmt->fetchColumn();
        $total += ($price * $quantity);
    }
    return $total;
}

// Process order
function processOrder($email, $address) {
    if (!isset($_SESSION['cart'])) {
        return false;
    }

    // Insert order into database
    $stmt = $conn->prepare("INSERT INTO orders (email, address, total_amount, created_at) VALUES (?, ?, ?, NOW())");
    $totalAmount = calculateTotal();
    $stmt->execute([$email, $address, $totalAmount]);
    $orderId = $conn->lastInsertId();

    // Insert order items
    foreach ($_SESSION['cart'] as $productId => $quantity) {
        $stmt = $conn->prepare("INSERT INTO order_items (order_id, product_id, quantity, price) VALUES (?, ?, ?, ?)");
        $price = getProductPrice($productId);
        $stmt->execute([$orderId, $productId, $quantity, $price]);
    }

    // Clear cart
    unset($_SESSION['cart']);
    
    return true;
}

// Get product price from database
function getProductPrice($productId) {
    global $conn;
    $stmt = $conn->prepare("SELECT price FROM products WHERE id = ?");
    $stmt->execute([$productId]);
    return $stmt->fetchColumn();
}
