}

// Order processing function
function processOrder($order_data) {
    global $conn;
    
    // Insert order into orders table
    $sql = "INSERT INTO orders (user_id, total_amount, payment_method, order_date) 
            VALUES (?, ?, ?, NOW())";
    $stmt = $conn->prepare($sql);
    $stmt->execute([$order_data['user_id'], $order_data['total_amount'], $order_data['payment_method']]);
    
    // Get last inserted order ID
    $order_id = $conn->lastInsertId();
    
    // Insert order items into order_items table
    foreach ($order_data['items'] as $item) {
        $sql = "INSERT INTO order_items (order_id, product_id, quantity, price)
                VALUES (?, ?, ?, ?)";
        $stmt = $conn->prepare($sql);
        $stmt->execute([$order_id, $item['product_id'], $item['quantity'], $item['price']]);
    }
    
    // Update product stock
    foreach ($order_data['items'] as $item) {
        $sql = "UPDATE products SET stock = stock - ? WHERE id = ?";
        $stmt = $conn->prepare($sql);
        $stmt->execute([$item['quantity'], $item['product_id']]);
    }
    
    return $order_id;
}

// Invoice generation function
function generateInvoice($order_id) {
    global $conn;
    
    // Get order details
    $sql = "SELECT * FROM orders WHERE id = ?";
    $stmt = $conn->prepare($sql);
    $stmt->execute([$order_id]);
    $order = $stmt->fetch();
    
    // Get order items
    $sql = "SELECT * FROM order_items WHERE order_id = ?";
    $stmt = $conn->prepare($sql);
    $stmt->execute([$order_id]);
    $items = $stmt->fetchAll();
    
    // Generate invoice HTML
    $invoice_html = "<html>
                        <head>
                            <title>Invoice</title>
                        </head>
                        <body>
                            <h1>Invoice #{$order['id']}</h1>
                            <p>Date: {$order['order_date']}</p>
                            <p>Total Amount: $" . number_format($order['total_amount'], 2) . "</p>
                            <table border='1'>
                                <tr><th>Product</th><th>Quantity</th><th>Price</th></tr>";
    
    foreach ($items as $item) {
        $invoice_html .= "<tr>
                            <td>" . getProductDetails($item['product_id'])['name'] . "</td>
                            <td>{$item['quantity']}</td>
                            <td>$" . number_format($item['price'], 2) . "</td>
                         </tr>";
    }
    
    $invoice_html .= "</table>
                        </body>
                    </html>";
    
    return $invoice_html;
}

// Helper function to get product details
function getProductDetails($product_id) {
    global $conn;
    
    $sql = "SELECT * FROM products WHERE id = ?";
    $stmt = $conn->prepare($sql);
    $stmt->execute([$product_id]);
    return $stmt->fetch();
}
