<?php
// database configuration
$host = 'localhost';
$username = 'root';
$password = '';
$db_name = 'purchase_system';

// connect to database
$conn = new mysqli($host, $username, $password, $db_name);

// check connection
if ($conn->connect_error) {
    die("Connection failed: " . $conn->connect_error);
}

// create products table
$sql = "CREATE TABLE IF NOT EXISTS products (
    id INT AUTO_INCREMENT PRIMARY KEY,
    name VARCHAR(255),
    description TEXT,
    price DECIMAL(10, 2),
    quantity INT
)";

$conn->query($sql);

// create orders table
$sql = "CREATE TABLE IF NOT EXISTS orders (
    id INT AUTO_INCREMENT PRIMARY KEY,
    user_id INT,
    product_id INT,
    quantity INT,
    total_price DECIMAL(10, 2),
    purchase_date TIMESTAMP DEFAULT CURRENT_TIMESTAMP
)";

$conn->query($sql);

// function to add item to cart
function addToCart($product_id, $quantity) {
    global $conn;
    
    // check if user is logged in
    if (!isset($_SESSION['user'])) {
        return "Please login to continue.";
    }
    
    $user_id = $_SESSION['user']['id'];
    
    // check product availability
    $sql = "SELECT quantity FROM products WHERE id = ?";
    $stmt = $conn->prepare($sql);
    $stmt->bind_param("i", $product_id);
    $stmt->execute();
    $result = $stmt->get_result();
    
    if ($result->num_rows > 0) {
        $row = $result->fetch_assoc();
        
        if ($quantity > $row['quantity']) {
            return "Not enough stock available.";
        }
        
        // add to cart
        $sql = "INSERT INTO orders (user_id, product_id, quantity, total_price) VALUES (?, ?, ?, ?)";
        $stmt = $conn->prepare($sql);
        $price_sql = "SELECT price FROM products WHERE id = ?";
        $price_stmt = $conn->prepare($price_sql);
        $price_stmt->bind_param("i", $product_id);
        $price_stmt->execute();
        $price_result = $price_stmt->get_result();
        $price_row = $price_result->fetch_assoc();
        $total_price = $quantity * $price_row['price'];
        
        $stmt->bind_param("iiid", $user_id, $product_id, $quantity, $total_price);
        if ($stmt->execute()) {
            return "Item added to cart successfully.";
        } else {
            return "Error adding item to cart.";
        }
    } else {
        return "Product not found.";
    }
}

// function to update quantity in cart
function updateCart($order_id, $quantity) {
    global $conn;
    
    // check if user is logged in
    if (!isset($_SESSION['user'])) {
        return "Please login to continue.";
    }
    
    // update quantity
    $sql = "UPDATE orders SET quantity = ? WHERE id = ?";
    $stmt = $conn->prepare($sql);
    $stmt->bind_param("ii", $quantity, $order_id);
    
    if ($stmt->execute()) {
        return "Quantity updated successfully.";
    } else {
        return "Error updating quantity.";
    }
}

// function to remove item from cart
function removeFromCart($order_id) {
    global $conn;
    
    // check if user is logged in
    if (!isset($_SESSION['user'])) {
        return "Please login to continue.";
    }
    
    // delete order
    $sql = "DELETE FROM orders WHERE id = ?";
    $stmt = $conn->prepare($sql);
    $stmt->bind_param("i", $order_id);
    
    if ($stmt->execute()) {
        return "Item removed from cart successfully.";
    } else {
        return "Error removing item from cart.";
    }
}

// function to purchase items
function purchaseItems() {
    global $conn;
    
    // check if user is logged in
    if (!isset($_SESSION['user'])) {
        return "Please login to continue.";
    }
    
    $user_id = $_SESSION['user']['id'];
    
    // get all orders for this user
    $sql = "SELECT * FROM orders WHERE user_id = ?";
    $stmt = $conn->prepare($sql);
    $stmt->bind_param("i", $user_id);
    $stmt->execute();
    $result = $stmt->get_result();
    
    if ($result->num_rows > 0) {
        // update product quantities
        while ($row = $result->fetch_assoc()) {
            $product_id = $row['product_id'];
            $quantity = $row['quantity'];
            
            $sql = "UPDATE products SET quantity = quantity - ? WHERE id = ?";
            $update_stmt = $conn->prepare($sql);
            $update_stmt->bind_param("ii", $quantity, $product_id);
            $update_stmt->execute();
        }
        
        // clear cart
        $sql = "DELETE FROM orders WHERE user_id = ?";
        $delete_stmt = $conn->prepare($sql);
        $delete_stmt->bind_param("i", $user_id);
        $delete_stmt->execute();
        
        return "Purchase completed successfully.";
    } else {
        return "Your cart is empty.";
    }
}

// function to display cart
function displayCart() {
    global $conn;
    
    // check if user is logged in
    if (!isset($_SESSION['user'])) {
        return "Please login to continue.";
    }
    
    $user_id = $_SESSION['user']['id'];
    
    // get all orders for this user
    $sql = "SELECT * FROM orders WHERE user_id = ?";
    $stmt = $conn->prepare($sql);
    $stmt->bind_param("i", $user_id);
    $stmt->execute();
    $result = $stmt->get_result();
    
    if ($result->num_rows > 0) {
        $cart_items = array();
        
        while ($row = $result->fetch_assoc()) {
            // get product details
            $sql = "SELECT name, price FROM products WHERE id = ?";
            $product_stmt = $conn->prepare($sql);
            $product_stmt->bind_param("i", $row['product_id']);
            $product_stmt->execute();
            $product_result = $product_stmt->get_result();
            $product_row = $product_result->fetch_assoc();
            
            $cart_item = array(
                'id' => $row['id'],
                'name' => $product_row['name'],
                'price' => $product_row['price'],
                'quantity' => $row['quantity'],
                'total_price' => $row['total_price']
            );
            
            array_push($cart_items, $cart_item);
        }
        
        return $cart_items;
    } else {
        return "Your cart is empty.";
    }
}

// function to calculate total price
function calculateTotal() {
    global $conn;
    
    // check if user is logged in
    if (!isset($_SESSION['user'])) {
        return 0;
    }
    
    $user_id = $_SESSION['user']['id'];
    
    // get sum of all total prices for this user
    $sql = "SELECT SUM(total_price) AS total FROM orders WHERE user_id = ?";
    $stmt = $conn->prepare($sql);
    $stmt->bind_param("i", $user_id);
    $stmt->execute();
    $result = $stmt->get_result();
    
    if ($row = $result->fetch_assoc()) {
        return $row['total'];
    } else {
        return 0;
    }
}

// function to login user
function loginUser($username, $password) {
    global $conn;
    
    // hash password
    $hashed_password = md5($password);
    
    // check if user exists
    $sql = "SELECT * FROM users WHERE username = ? AND password = ?";
    $stmt = $conn->prepare($sql);
    $stmt->bind_param("ss", $username, $hashed_password);
    $stmt->execute();
    $result = $stmt->get_result();
    
    if ($result->num_rows > 0) {
        $row = $result->fetch_assoc();
        
        // start session and store user details
        session_start();
        $_SESSION['user'] = array(
            'id' => $row['id'],
            'username' => $row['username']
        );
        
        return "Login successful.";
    } else {
        return "Invalid username or password.";
    }
}

// function to register user
function registerUser($username, $password) {
    global $conn;
    
    // check if user already exists
    $sql = "SELECT * FROM users WHERE username = ?";
    $stmt = $conn->prepare($sql);
    $stmt->bind_param("s", $username);
    $stmt->execute();
    $result = $stmt->get_result();
    
    if ($result->num_rows > 0) {
        return "Username already exists.";
    } else {
        // hash password
        $hashed_password = md5($password);
        
        // insert new user
        $sql = "INSERT INTO users (username, password) VALUES (?, ?)";
        $stmt = $conn->prepare($sql);
        $stmt->bind_param("ss", $username, $hashed_password);
        
        if ($stmt->execute()) {
            session_start();
            $_SESSION['user'] = array(
                'id' => $stmt->insert_id,
                'username' => $username
            );
            
            return "Registration successful.";
        } else {
            return "Error registering user.";
        }
    }
}

// main script
if (isset($_POST['action'])) {
    switch ($_POST['action']) {
        case 'login':
            $result = loginUser($_POST['username'], $_POST['password']);
            echo $result;
            break;
            
        case 'register':
            $result = registerUser($_POST['username'], $_POST['password']);
            echo $result;
            break;
            
        case 'add_to_cart':
            $result = addToCart($_POST['product_id'], $_POST['quantity']);
            echo $result;
            break;
            
        case 'update_cart':
            $result = updateCart($_POST['order_id'], $_POST['quantity']);
            echo $result;
            break;
            
        case 'remove_from_cart':
            $result = removeFromCart($_POST['order_id']);
            echo $result;
            break;
            
        case 'purchase':
            $result = purchaseItems();
            echo $result;
            break;
    }
}

// close database connection
$conn->close();
?>


