<?php 
// Database configuration
$servername = "localhost";
$username = "root";
$password = "";
$dbname = "store";

// Create connection
$conn = new mysqli($servername, $username, $password, $dbname);

// Check connection
if ($conn->connect_error) {
    die("Connection failed: " . $conn->connect_error);
}

// Create products table if not exists
$sql = "CREATE TABLE IF NOT EXISTS products (
id INT AUTO_INCREMENT PRIMARY KEY,
name VARCHAR(255),
price DECIMAL(10, 2)
)";
$conn->query($sql);

// Create shopping cart table if not exists
$sql = "CREATE TABLE IF NOT EXISTS shopping_cart (
id INT AUTO_INCREMENT PRIMARY KEY,
product_id INT,
quantity INT,
user_id INT
)";
$conn->query($sql);

// Create orders table if not exists
$sql = "CREATE TABLE IF NOT EXISTS orders (
id INT AUTO_INCREMENT PRIMARY KEY,
order_date DATETIME,
total DECIMAL(10, 2),
status VARCHAR(50)
)";
$conn->query($sql);

// Create order items table if not exists
$sql = "CREATE TABLE IF NOT EXISTS order_items (
id INT AUTO_INCREMENT PRIMARY KEY,
order_id INT,
product_id INT,
quantity INT,
price DECIMAL(10, 2)
)";
$conn->query($sql);

// Product class
class Product {
    private $conn;

    public function __construct($db_connection) {
        $this->conn = $db_connection;
    }

    // Get all products
    public function getProducts() {
        $stmt = $this->conn->prepare("SELECT * FROM products");
        $stmt->execute();
        return $stmt->get_result()->fetch_all(MYSQLI_ASSOC);
    }

    // Add product to database
    public function addProduct($name, $price) {
        $stmt = $this->conn->prepare("INSERT INTO products (name, price) VALUES (?, ?)");
        $stmt->bind_param("ss", $name, $price);
        return $stmt->execute();
    }

    // Update product quantity
    public function updateProduct($id, $quantity) {
        $stmt = $this->conn->prepare("UPDATE products SET quantity = ? WHERE id = ?");
        $stmt->bind_param("is", $quantity, $id);
        return $stmt->execute();
    }
}

// Shopping cart class
class ShoppingCart {
    private $db;

    public function __construct($db_connection) {
        $this->db = $db_connection;
    }

    // Add item to cart
    public function addToCart($product_id, $quantity) {
        if (isset($_SESSION['cart'])) {
            $cart = $_SESSION['cart'];
        } else {
            $cart = array();
        }
        
        if (!array_key_exists($product_id, $cart)) {
            $cart[$product_id] = $quantity;
        } else {
            $cart[$product_id] += $quantity;
        }
        
        $_SESSION['cart'] = $cart;
    }

    // Update cart quantity
    public function updateCart($product_id, $quantity) {
        if (isset($_SESSION['cart'][$product_id])) {
            $_SESSION['cart'][$product_id] = $quantity;
        }
    }

    // Remove item from cart
    public function removeItem($product_id) {
        unset($_SESSION['cart'][$product_id]);
    }

    // Get total price of items in cart
    public function getTotal() {
        $total = 0;
        
        if (isset($_SESSION['cart'])) {
            foreach ($_SESSION['cart'] as $product_id => $quantity) {
                $sql = "SELECT price FROM products WHERE id = ?";
                $stmt = $this->db->prepare($sql);
                $stmt->bind_param("i", $product_id);
                $stmt->execute();
                $result = $stmt->get_result();
                $price = $result->fetch_assoc()['price'];
                
                $total += ($price * $quantity);
            }
        }
        
        return $total;
    }

    // Get items in cart
    public function getCartItems() {
        if (isset($_SESSION['cart'])) {
            foreach ($_SESSION['cart'] as $product_id => $quantity) {
                $sql = "SELECT * FROM products WHERE id = ?";
                $stmt = $this->db->prepare($sql);
                $stmt->bind_param("i", $product_id);
                $stmt->execute();
                $result = $stmt->get_result()->fetch_assoc();
                
                $items[] = array(
                    'id' => $result['id'],
                    'name' => $result['name'],
                    'price' => $result['price'],
                    'quantity' => $quantity
                );
            }
            
            return $items;
        } else {
            return array();
        }
    }
}

// Initialize classes
$product = new Product($conn);
$cart = new ShoppingCart($conn);

// Add some sample products to database
if ($product->addProduct("Laptop", 999.99)) { echo "Product added"; }

// Display products
$products = $product->getProducts();
foreach ($products as $p) {
    echo "<div>";
    echo "<h3>".$p['name']."</h3>";
    echo "<p>Price: $".$p['price']."</p>";
    echo "<a href='add_to_cart.php?id=".$p['id']."'>Add to Cart</a>";
    echo "</div>";
}

// Display cart
$cartItems = $cart->getCartItems();
foreach ($cartItems as $item) {
    echo "<div>";
    echo "<h3>".$item['name']."</h3>";
    echo "<p>Price: $".$item['price']."</p>";
    echo "<p>Quantity: ".$item['quantity']."</p>";
    echo "</div>";
}

// Checkout process
if (isset($_POST['checkout'])) {
    // Process payment
    if ($cart->getTotal() > 0) {
        // Insert order into orders table
        $sql = "INSERT INTO orders (order_date, total, status) VALUES (NOW(), ?, 'pending')";
        $stmt = $conn->prepare($sql);
        $stmt->bind_param("d", $cart->getTotal());
        $stmt->execute();
        
        // Get order id
        $order_id = $conn->insert_id;
        
        // Insert order items into order_items table
        foreach ($cartItems as $item) {
            $sql = "INSERT INTO order_items (order_id, product_id, quantity, price) VALUES (?, ?, ?, ?)";
            $stmt = $conn->prepare($sql);
            $stmt->bind_param("iiid", $order_id, $item['id'], $item['quantity'], $item['price']);
            $stmt->execute();
        }
        
        // Clear cart
        unset($_SESSION['cart']);
        
        echo "Thank you for your order!";
    } else {
        echo "Your cart is empty!";
    }
}
?>


