<?php
// Database connection
$host = 'localhost';
$username = 'root';
$password = '';
$dbname = 'shopping_cart';

$conn = new mysqli($host, $username, $password, $dbname);

if ($conn->connect_error) {
    die("Connection failed: " . $conn->connect_error);
}

// Get products from database
function getProducts() {
    global $conn;
    $sql = "SELECT * FROM products";
    $result = $conn->query($sql);
    
    if ($result->num_rows > 0) {
        return $result;
    } else {
        return false;
    }
}

// Add item to cart
function addToCart($productId, $quantity) {
    global $conn;
    $product = getProductById($productId);
    
    if ($product && isset($_SESSION['cart'])) {
        $_SESSION['cart'][$productId] += $quantity;
    } else {
        $_SESSION['cart'] = array();
        $_SESSION['cart'][$productId] = $quantity;
    }
}

// Get product by ID
function getProductById($id) {
    global $conn;
    $sql = "SELECT * FROM products WHERE id = ?";
    $stmt = $conn->prepare($sql);
    $stmt->bind_param("i", $id);
    $stmt->execute();
    $result = $stmt->get_result();
    
    if ($result->num_rows > 0) {
        return $result->fetch_assoc();
    } else {
        return false;
    }
}

// Remove item from cart
function removeFromCart($productId) {
    global $conn;
    if (isset($_SESSION['cart'][$productId])) {
        unset($_SESSION['cart'][$productId]);
    }
}

// Update cart item quantity
function updateCartItem($productId, $quantity) {
    if ($quantity > 0) {
        $_SESSION['cart'][$productId] = $quantity;
    } else {
        removeFromCart($productId);
    }
}

// Calculate total price
function calculateTotal() {
    global $conn;
    $total = 0;
    
    foreach ($_SESSION['cart'] as $productId => $quantity) {
        $product = getProductById($productId);
        $total += ($product['price'] * $quantity);
    }
    
    return $total;
}

// Process payment
function processPayment($cardNumber, $cardHolder, $expiryDate, $cvv) {
    // Simulate payment processing
    sleep(2); // Wait for 2 seconds
    
    if (strlen($cardNumber) == 16 && strlen($cvv) == 3) {
        return true;
    } else {
        return false;
    }
}

// Save order to database
function saveOrder($products, $totalAmount) {
    global $conn;
    date_default_timezone_set('UTC');
    
    $orderDate = date("Y-m-d H:i:s");
    
    // Insert into orders table
    $sql = "INSERT INTO orders (order_date, total_amount) VALUES (?, ?)";
    $stmt = $conn->prepare($sql);
    $stmt->bind_param("ss", $orderDate, $totalAmount);
    $stmt->execute();
    
    $orderId = $stmt->insert_id;
    
    // Insert into order_items table
    foreach ($products as $productId => $quantity) {
        $product = getProductById($productId);
        
        $sql = "INSERT INTO order_items (order_id, product_id, quantity) VALUES (?, ?, ?)";
        $stmt = $conn->prepare($sql);
        $stmt->bind_param("iii", $orderId, $productId, $quantity);
        $stmt->execute();
    }
    
    return $orderId;
}
