// Function to display products
function displayProducts() {
    global $conn;
    $stmt = $conn->query("SELECT * FROM products");
    while ($row = $stmt->fetch()) {
        echo "<div class='product'>";
        echo "<h3>" . $row['name'] . "</h3>";
