    $update_query = "UPDATE products SET stock = stock - ? WHERE id = ?";
    $update_stmt = mysqli_prepare($conn, $update_query);
    mysqli_stmt_bind_param($update_stmt, "ii", $quantity, $product_id);
    mysqli_execute($update_stmt);
    
