<?php
session_start();

// Check if form is submitted
if ($_SERVER["REQUEST_METHOD"] == "POST") {
    // Get cart contents
    $cart = $_SESSION['cart'];
    
    // Validate required fields
    if (empty($_POST['name']) || empty($_POST['email']) || empty($_POST['phone'])) {
        header('Location: checkout.php');
        exit();
    }

    // Connect to database
    require_once 'products.php';

    try {
        // Start transaction
        $pdo->beginTransaction();

        // Insert order into orders table
        $orderStmt = $pdo->prepare("INSERT INTO orders (name, email, phone, address, city, state, zip_code) VALUES (?, ?, ?, ?, ?, ?, ?)");
        $orderStmt->execute([
            $_POST['name'],
            $_POST['email'],
            $_POST['phone'],
            $_POST['address'],
            $_POST['city'],
            $_POST['state'],
            $_POST['zip_code']
        ]);
        
        $orderId = $pdo->lastInsertId();

        // Insert order items into order_items table
        foreach ($cart as $item) {
            $itemStmt = $pdo->prepare("INSERT INTO order_items (order_id, product_name, price, quantity) VALUES (?, ?, ?, ?)");
            $itemStmt->execute([
                $orderId,
                $item['name'],
                $item['price'],
                $item['quantity']
            ]);
        }

        // Update stock levels
        foreach ($cart as $id => $item) {
            $updateStock = $pdo->prepare("UPDATE products SET stock = stock - ? WHERE id = ?");
            $updateStock->execute([$item['quantity'], $id]);
        }

        // Commit transaction
        $pdo->commit();

        // Clear cart
        unset($_SESSION['cart']);

        // Redirect to thank you page
        header('Location: thankyou.php');
        exit();
    } catch (PDOException $e) {
        // Rollback if error occurs
        $pdo->rollBack();
        die("Error occurred during checkout: " . $e->getMessage());
    }
}
