    echo "<p>Your order has been received. Order Summary:</p>";

    $total = 0;
    foreach ($_SESSION['cart'] as $item) {
        $total += ($item['price'] * $item['quantity']);
        echo "<li>".$item['name']." x ".$item['quantity']."</li>";
    }
    echo "<p>Total: \$".$total."</p>";

