// Simple database connection class
class Database {
    private $host = 'localhost';
    private $db_name = 'purchase_system';
    private $username = 'root';
    private $password = '';
    public $conn;

    function __construct() {
        $this->conn = null;
        try {
            $this->conn = new PDO("mysql:host=" . $this->host . ";dbname=" . $this->db_name, $this->username, $this->password);
            $this->conn->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);
        } catch (PDOException $e) {
            echo "Connection failed: " . $e->getMessage();
        }
    }

    function getConnection() {
        return $this->conn;
    }
}

// User authentication class
class Auth {
    private $db;

    public function __construct($db) {
        $this->db = $db;
    }

    // Register user
    public function register($username, $email, $password) {
        try {
            $stmt = $this->db->prepare("INSERT INTO users (username, email, password) VALUES (:username, :email, :password)");
            $stmt->bindParam(':username', $username);
            $stmt->bindParam(':email', $email);
            $stmt->bindParam(':password', password_hash($password, PASSWORD_DEFAULT));
            return $stmt->execute();
        } catch (PDOException $e) {
            echo "Registration error: " . $e->getMessage();
            return false;
        }
    }

    // Login user
    public function login($email, $password) {
        try {
            $stmt = $this->db->prepare("SELECT * FROM users WHERE email = :email");
            $stmt->bindParam(':email', $email);
            $result = $stmt->execute();
            $user = $stmt->fetch(PDO::FETCH_ASSOC);

            if ($user && password_verify($password, $user['password'])) {
                $_SESSION['logged_in'] = true;
                $_SESSION['user_id'] = $user['id'];
                $_SESSION['username'] = $user['username'];
                return true;
            }
            return false;
        } catch (PDOException $e) {
            echo "Login error: " . $e->getMessage();
            return false;
        }
    }

    // Check if user is logged in
    public function isLoggedIn() {
        return isset($_SESSION['logged_in']);
    }

    // Logout user
    public function logout() {
        session_unset();
        session_destroy();
        header("Location: login.php");
        exit;
    }
}

// Product class
class Product {
    private $db;

    public function __construct($db) {
        $this->db = $db;
    }

    // Get all products
    public function getAllProducts() {
        try {
            $stmt = $this->db->prepare("SELECT * FROM products");
            $stmt->execute();
            return $stmt->fetchAll(PDO::FETCH_ASSOC);
        } catch (PDOException $e) {
            echo "Error fetching products: " . $e->getMessage();
            return [];
        }
    }

    // Get product by ID
    public function getProductById($id) {
        try {
            $stmt = $this->db->prepare("SELECT * FROM products WHERE id = :id");
            $stmt->bindParam(':id', $id);
            $stmt->execute();
            return $stmt->fetch(PDO::FETCH_ASSOC);
        } catch (PDOException $e) {
            echo "Error fetching product: " . $e->getMessage();
            return null;
        }
    }
}

// Cart class
class Cart {
    public function __construct() {
        if (!isset($_SESSION['cart'])) {
            $_SESSION['cart'] = array();
        }
    }

    // Add item to cart
    public function addItem($id, $quantity) {
        try {
            $db = new Database();
            $product = new Product($db->getConnection());
            $productData = $product->getProductById($id);

            if ($productData && $quantity > 0) {
                $_SESSION['cart'][$id] = array(
                    'name' => $productData['name'],
                    'price' => $productData['price'],
                    'quantity' => $quantity
                );
                return true;
            }
            return false;
        } catch (Exception $e) {
            echo "Error adding item: " . $e->getMessage();
            return false;
        }
    }

    // Remove item from cart
    public function removeItem($id) {
        try {
            unset($_SESSION['cart'][$id]);
            return true;
        } catch (Exception $e) {
            echo "Error removing item: " . $e->getMessage();
            return false;
        }
    }

    // Get total price of items in cart
    public function getTotal() {
        $total = 0;
        foreach ($_SESSION['cart'] as $item) {
            $total += ($item['price'] * $item['quantity']);
        }
        return $total;
    }

    // Get all items in cart
    public function getItems() {
        return $_SESSION['cart'];
    }
}

// Order class
class Order {
    private $db;

    public function __construct($db) {
        $this->db = $db;
    }

    // Place order
    public function placeOrder($userId, $products, $totalPrice) {
        try {
            $stmt = $this->db->prepare("INSERT INTO orders (user_id, products, total_price, created_at) VALUES (:user_id, :products, :total_price, NOW())");
            $stmt->bindParam(':user_id', $userId);
            $stmt->bindParam(':products', serialize($products));
            $stmt->bindParam(':total_price', $totalPrice);
            return $stmt->execute();
        } catch (PDOException $e) {
            echo "Error placing order: " . $e->getMessage();
            return false;
        }
    }
}

// CSRF protection
function generateCsrfToken() {
    if (empty($_SESSION['csrf_token'])) {
        $_SESSION['csrf_token'] = bin2hex(random_bytes(32));
    }
    return $_SESSION['csrf_token'];
}

function validateCsrfToken($token) {
    if (!isset($_SESSION['csrf_token']) || $token !== $_SESSION['csrf_token']) {
        die("Invalid CSRF token");
    }
}


