<?php
require_once("config.php");

class Product {
    public $id;
    public $name;
    public $description;
    public $price;
    public $stock;
    public $image_url;

    public function __construct($id, $name, $description, $price, $stock, $image_url) {
        $this->id = $id;
        $this->name = $name;
        $this->description = $description;
        $this->price = $price;
        $this->stock = $stock;
        $this->image_url = $image_url;
    }

    public static function getAllProducts() {
        global $conn;
        
        $products = array();
        
        $stmt = $conn->prepare("SELECT * FROM products");
        $stmt->execute();
        $result = $stmt->get_result();

        while ($row = $result->fetch_assoc()) {
            $product = new Product($row['id'], $row['name'], $row['description'], $row['price'], $row['stock'], $row['image_url']);
            array_push($products, $product);
        }

        return $products;
    }
}
?>


<?php
session_start();

class Cart {
    public $items;

    function __construct() {
        if (isset($_SESSION['cart'])) {
            $this->items = $_SESSION['cart'];
        } else {
            $this->items = array();
        }
    }

    public function addToCart($productId, $quantity) {
        // Check if product exists in cart
        if (!empty($this->items[$productId])) {
            // Update quantity
            $this->items[$productId] += $quantity;
        } else {
            // Add new product
            $this->items[$productId] = $quantity;
        }

        $_SESSION['cart'] = $this->items;
    }

    public function updateCart($productId, $quantity) {
        if ($quantity > 0) {
            $this->items[$productId] = $quantity;
        } else {
            unset($this->items[$productId]);
        }

        $_SESSION['cart'] = $this->items;
    }

    public function getCartTotal() {
        global $conn;

        $total = 0;
        
        foreach ($this->items as $productId => $quantity) {
            // Get product price
            $stmt = $conn->prepare("SELECT price FROM products WHERE id = ?");
            $stmt->bind_param("i", $productId);
            $stmt->execute();
            $result = $stmt->get_result();

            if ($row = $result->fetch_assoc()) {
                $total += $row['price'] * $quantity;
            }
        }

        return $total;
    }

    public function getCartItems() {
        global $conn;

        $items = array();
        
        foreach ($this->items as $productId => $quantity) {
            // Get product details
            $stmt = $conn->prepare("SELECT * FROM products WHERE id = ?");
            $stmt->bind_param("i", $productId);
            $stmt->execute();
            $result = $stmt->get_result();

            if ($row = $result->fetch_assoc()) {
                $product = new Product($row['id'], $row['name'], $row['description'], $row['price'], $row['stock'], $row['image_url']);
                array_push($items, array(
                    'product' => $product,
                    'quantity' => $quantity
                ));
            }
        }

        return $items;
    }
}
?>


<?php
require_once("config.php");
session_start();

class Order {
    public $id;
    public $user_id;
    public $total_amount;
    public $payment_method;
    public $order_status;

    function __construct($user_id, $total_amount, $payment_method) {
        global $conn;

        // Create new order
        $stmt = $conn->prepare("INSERT INTO orders (user_id, total_amount, payment_method, order_status) VALUES (?, ?, ?, 'pending')");
        $stmt->bind_param("ids", $user_id, $total_amount, $payment_method);
        
        if ($stmt->execute()) {
            $this->id = $conn->insert_id;
            $this->user_id = $user_id;
            $this->total_amount = $total_amount;
            $this->payment_method = $payment_method;
            $this->order_status = 'pending';
            
            // Insert order items
            $cart = new Cart();
            foreach ($cart->getCartItems() as $item) {
                $productId = $item['product']->id;
                $quantity = $item['quantity'];
                $price = $item['product']->price;

                $stmt2 = $conn->prepare("INSERT INTO order_items (order_id, product_id, quantity, price) VALUES (?, ?, ?, ?)");
                $stmt2->bind_param("iidi", $this->id, $productId, $quantity, $price);
                $stmt2->execute();
            }

            // Deduct stock
            foreach ($cart->getCartItems() as $item) {
                $productId = $item['product']->id;
                $quantity = $item['quantity'];

                $stmt3 = $conn->prepare("UPDATE products SET stock = stock - ? WHERE id = ?");
                $stmt3->bind_param("ii", $quantity, $productId);
                $stmt3->execute();
            }

            // Clear cart
            $_SESSION['cart'] = array();

            return true;
        } else {
            return false;
        }
    }
}
